statements.select:
/* EntrprsDac.SelectEntrprsList */ SELECT * FROM ( SELECT A.* ,ROWNUM AS "RowNumber" ,COUNT(*) OVER() AS "TotalCount" FROM ( SELECT A.ENTRPRS_SN AS "EntrprsSn" /* 기업일련번호 */ , ENTRPRS_NM AS "EntrprsNm" /* 기업명 */ , BIZRNO AS "Bizrno" /* 사업자등록번호 */ , RPRSNTV_NM AS "RprsntvNm" /* 대표자명 */ , REPRSNT_TELNO AS "ReprsntTelno" /* 대표전화번호 */ , REPRSNT_FXNUM AS "ReprsntFxNum" /* 대표팩스번호 */ , MFCRTR_AT AS "MfcrtrAt" /* 제조업여부 */ , IRTB_AT AS "IrtbAt" /* 수입업여부 */ , REPAIR_INDUTY_AT AS "RepairIndutyAt" /* 수리업여부 */ , MESUR_PROOF_INDUTY_AT AS "MesurProofIndutyAt" /* 계량증명업여부 */ , MRNR_EMPLYR_AT AS "MrnrEmplyrAt" /* 계량기사용자여부 */ , ATPT_RCEPT_AT AS "AtptRceptAt" /* 시도접수여부 */ , MBER_AT AS "MberAt" /* 회원여부 */ , DELETE_AT AS "DeleteAt" /* 삭제여부 */ , FN_ENTRPRS_SE_NM('00',A.ENTRPRS_SN) AS "EnterpriseSectionName" /* 업체구분명 */ , REGIST_DT AS "RegistDt" /* 등록일시 */ , REGISTER_ID AS "RegisterId" /* 등록자ID */ , UPDT_DT AS "UpdtDt" /* 수정일시 */ , UPDUSR_ID AS "UpdusrId" /* 수정자ID */ , REGISTER_SE_CODE AS "RegisterSeCode" /* 등록자구분코드 */ , UPDUSR_SE_CODE AS "UpdusrSeCode" /* 수정자구분코드 */ , FN_USER_NM(REGISTER_SE_CODE, REGISTER_ID) AS "RegisterNm" /* 등록자명 */ , FN_USER_NM(UPDUSR_SE_CODE, UPDUSR_ID) AS "UpdusrNm" /* 수정자명 */ , HMPG_URL AS "HmpgUrl" /* 홈페이지URL */ , NVL(RCOGN_DE, '미확인') AS "RcognDe" /* 인정일자 */ , NVL(FN_CMMN_CODE_NM(RCOGN_SE_CODE), '미확인') AS "RcognSeCodeNm" /* 업체구분 */ FROM TN_ENTRPRS A Left Outer Join TN_ENTRPRS_RCOGN_INFO B On A.ENTRPRS_SN = B.ENTRPRS_SN WHERE DELETE_AT = 'N'
AND MBER_AT = #SearchMberAt# /* 회원여부 */
AND RPRSNTV_NM LIKE '%' || #SearchRprsntvNm# || '%'
AND ENTRPRS_NM LIKE '%' || #SearchEntrprsNm# || '%'
AND BIZRNO LIKE '%' || #SearchBizrNO# || '%'
AND ENTRPRS_SN IN ( SELECT ENTRPRS_SN FROM TN_ENTRPRS_ADRES WHERE SUBSTR(ZIP, 0, 2) IN ( SELECT COLUMN_VALUE FROM TABLE(FN_ATPT_TO_ZIP(#SearchAtptSeCode#)) )
AND ZIP LIKE FN_GUGUN_TO_ZIP(#SearchGuGunSeCode#) || '%'
)
AND A.ENTRPRS_SN IN ( SELECT ENTRPRS_SN FROM TN_ENTRPRS_RCOGN_INFO WHERE RCOGN_DE BETWEEN #SearchStartDate# AND #SearchEndDate# GROUP BY ENTRPRS_SN )
/* 제조/수리/수입업 여부 */
AND ( (MFCRTR_AT = 'Y') OR (IRTB_AT = 'Y') OR (REPAIR_INDUTY_AT = 'Y') )
/* 구분 */
AND MFCRTR_AT = 'Y'
AND IRTB_AT = 'Y'
AND REPAIR_INDUTY_AT = 'Y'
/* 계량기종류코드 */ AND A.ENTRPRS_SN IN ( SELECT ENTRPRS_SN FROM TN_MNFCTUR_MRNR_INFO WHERE MRNR_KND_CODE = #SearchMrnrKndCode# )
/* 인정번호(제조,수리) */ AND A.ENTRPRS_SN IN ( SELECT ENTRPRS_SN FROM TN_ENTRPRS_RCOGN_INFO WHERE RCOGN_SE_CODE IN('AC028001','AC028002') AND RCOGN_NO_NM LIKE '%' || #SearchRcognNoNm# || '%' )
/* 계량증명업 여부 */
AND MESUR_PROOF_INDUTY_AT = #SearchMesurProofIndutyAt#
AND MESUR_PROOF_REGIST_DE BETWEEN #SearchStartDate# AND #SearchEndDate#
/* 계량기종류코드 */ AND A.ENTRPRS_SN IN ( SELECT ENTRPRS_SN FROM TN_MESUR_PROOF_INFO WHERE MRNR_KND_CODE = #SearchMrnrKndCode# )
/* 인정번호(계량증명업) */ AND A.ENTRPRS_SN IN ( SELECT ENTRPRS_SN FROM TN_ENTRPRS_RCOGN_INFO WHERE RCOGN_SE_CODE = 'AC028003' AND RCOGN_NO_NM LIKE '%' || #SearchRcognNoNm# || '%' )
ORDER BY A.ENTRPRS_SN DESC ) A ) WHERE "RowNumber" BETWEEN #StartIndex# AND #EndIndex#

/* EntrprsDac.SelectEntrprsAdresList */ SELECT ENTRPRS_ADRES_SN AS "EntrprsAdresSn" ,ENTRPRS_SN AS "EntrprsSn" ,ENTRPRS_ADRES_SE_CODE AS "EntrprsAdresSeCode" ,FN_CMMN_CODE_NM(ENTRPRS_ADRES_SE_CODE) AS "EntrprsAdresSeCodeNm" ,ENTRPRS_ADRES_NM AS "EntrprsAdresNm" ,ADRES_TELNO AS "AdresTelno" ,ADRES_FXNUM AS "AdresFxnum" ,ADRES_SE_CODE AS "AdresSeCode" ,DETAIL_ADRES AS "DetailAdres" ,BASS_ADRES AS "BassAdres" ,ZIP AS "Zip" ,SUBSTR(ZIP, 0, 3) AS "Zip1" ,SUBSTR(ZIP, 4, 3) AS "Zip2" ,DMSTC_AT AS "DmstcAt" FROM TN_ENTRPRS_ADRES WHERE ENTRPRS_SN = #EntrprsSn#
AND ENTRPRS_ADRES_SE_CODE = #EntrprsAdresSeCode#
ORDER BY ENTRPRS_ADRES_SN ASC
